function addSourceToVideo(element, type, dataURI) {
  var source = document.createElement("source");
  source.src = dataURI;
  source.type = "video/" + type;
  element.appendChild(source);
}

// NOTE: You must call this on a user interaction
function disableSleeping() {
  const noSleepVideo = document.createElement("video");
  noSleepVideo.setAttribute("loop", "");

  // Append nosleep video sources
  addSourceToVideo(noSleepVideo, "webm", media.WebM);
  addSourceToVideo(noSleepVideo, "mp4", media.MP4);

  noSleepVideo.play();
}

module.exports = disableSleeping;
